-- Процедура для расчета показателей продаж и выявления расхождений
DELIMITER //

CREATE PROCEDURE sp_report_1 (
    IN @date_from DATE,
    IN @date_to DATE,
    IN @good_group_name VARCHAR(109)
)
BEGIN
    -- Объявление переменных
    DECLARE v_total_sale_grs DECIMAL(10,2);
    DECLARE v_total_quantity DECIMAL(10,2);

    -- Выборка данных по продажам
    SELECT 
        SUM(CAST(sale_grs AS DECIMAL(10,2))) INTO v_total_sale_grs,
        SUM(CAST(quantity AS DECIMAL(10,2))) INTO v_total_quantity
    FROM fct_cheque
    JOIN dim_goods ON fct_cheque.good_id = dim_goods.good_id
    JOIN dim_date ON fct_cheque.date_id = dim_date.did
    WHERE dim_date.d BETWEEN @date_from AND @date_to
    AND dim_goods.group_name = @good_group_name;

    -- Вывод результатов и сообщение о расхождениях
    SELECT 
        v_total_sale_grs AS total_sale_grs,
        v_total_quantity AS total_quantity;

    IF v_total_sale_grs != 1782949.1 OR v_total_quantity != 6761.1 THEN
        SELECT 'Расхождение в данных о продажах!' AS message;
    END IF;

END //

DELIMITER ;


-- Процедура для добавления новых показателей в отчет
DELIMITER //

CREATE PROCEDURE sp_report_2 (
    IN @date_from DATE,
    IN @date_to DATE,
    IN @good_group_name VARCHAR(109)
)
BEGIN
    -- Создание временной таблицы для хранения результатов
    CREATE TEMPORARY TABLE temp_report AS
    SELECT
        dd.d AS sales_date,
        dg.good_name,
        dg.group_name,
        SUM(CAST(fc.quantity AS DECIMAL(10,2))) AS total_quantity,
        SUM(CAST(fc.sale_grs AS DECIMAL(10,2))) AS total_sale_grs,
        SUM(CAST(fc.cost_net AS DECIMAL(10,2))) AS total_cost_net
    FROM fct_cheque fc
    JOIN dim_goods dg ON fc.good_id = dg.good_id
    JOIN dim_date dd ON fc.date_id = dd.did
    WHERE dd.d BETWEEN @date_from AND @date_to
    AND dg.group_name = @good_group_name
    GROUP BY 1,2,3;

    -- Добавление рассчитанных показателей
    ALTER TABLE temp_report
    ADD COLUMN avg_purchase_price_net DECIMAL(10,2),
    ADD COLUMN margin_net DECIMAL(10,2),
    ADD COLUMN markup_percent_net DECIMAL(10,2);

    UPDATE temp_report
    SET avg_purchase_price_net = CASE WHEN total_quantity > 0 THEN total_cost_net / total_quantity ELSE 0 END,
        margin_net = total_sale_grs - total_cost_net,
        markup_percent_net = CASE WHEN total_cost_net > 0 THEN (total_sale_grs - total_cost_net) / total_cost_net * 100 ELSE 0 END;

    -- Вывод отчета
    SELECT * FROM temp_report;

    -- Удаление временной таблицы
    DROP TABLE temp_report;
END //

DELIMITER ;

