-- Задание 3: Фильтрация по нескольким группам товаров
CREATE PROCEDURE sp_report_1 (
    IN @date_from DATE,
    IN @date_to DATE,
    IN @good_group_names VARCHAR(255)
)
BEGIN
    DECLARE v_total_sale_grs DECIMAL(18,2);
    DECLARE v_total_quantity DECIMAL(18,2);
    DECLARE v_group_name VARCHAR(109);
    DECLARE done INT DEFAULT FALSE;
    DECLARE cur CURSOR FOR SELECT group_name FROM dim_goods WHERE group_name IN (SELECT value FROM str_split(@good_group_names, ','));
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    SET v_total_sale_grs = 0;
    SET v_total_quantity = 0;

    OPEN cur;
    read_loop: LOOP
        FETCH cur INTO v_group_name;
        IF done THEN
            LEAVE read_loop;
        END IF;
        SELECT 
            SUM(CAST(sale_grs AS DECIMAL(18,2))) INTO @v_sale,
            SUM(CAST(quantity AS DECIMAL(18,2))) INTO @v_quantity
        FROM fct_cheque fc
        JOIN dim_goods dg ON fc.good_id = dg.good_id
        JOIN dim_date dd ON fc.date_id = dd.did
        WHERE dd.d BETWEEN @date_from AND @date_to
        AND dg.group_name = v_group_name;

        SET v_total_sale_grs = v_total_sale_grs + @v_sale;
        SET v_total_quantity = v_total_quantity + @v_quantity;

    END LOOP;
    CLOSE cur;

    SELECT v_total_sale_grs, v_total_quantity;
END //


DELIMITER ;


-- Задание 4: Доля продаж по дням/магазинам/группам товаров
DELIMITER //

CREATE PROCEDURE sp_report_4 (
    IN @date_from DATE,
    IN @date_to DATE
)
BEGIN
    SELECT
        dd.td AS sales_date,
        ds.store_name,
        dg.group_name,
        SUM(CAST(fc.sale_grs AS DECIMAL(18,2))) AS daily_sales_grs,
        (SUM(CAST(fc.sale_grs AS DECIMAL(18,2))) / (SELECT SUM(CAST(sale_grs AS DECIMAL(18,2))) FROM fct_cheque fc2 JOIN dim_date dd2 ON fc2.date_id = dd2.did WHERE dd2.d BETWEEN @date_from AND @date_to) ) * 100 AS sales_share_percent
    FROM fct_cheque fc
    JOIN dim_goods dg ON fc.good_id = dg.good_id
    JOIN dim_date dd ON fc.date_id = dd.did
    JOIN dim_stores ds ON fc.store_id = ds.store_id
    WHERE dd.d BETWEEN @date_from AND @date_to
    GROUP BY 1,2,3
    ORDER BY sales_share_percent DESC;

END //

DELIMITER ;

-- вспомогательная функция для разбиения строки на массив значений
DELIMITER //
CREATE FUNCTION str_split(x VARCHAR(255), delim VARCHAR(12))
RETURNS SET OF VARCHAR(12)
BEGIN
  DECLARE i INT DEFAULT 1;
  DECLARE result VARCHAR(12);
  DECLARE pos INT DEFAULT 1;
  DECLARE len INT;

  SET len = LENGTH(x);

  WHILE i <= len DO
    SET pos = LOCATE(delim, SUBSTRING(x, i));
    IF pos = 0 THEN
      SET result = SUBSTRING(x, i);
      SET i = i + LENGTH(result);
    ELSE
      SET result = SUBSTRING(x, i, pos - 1);
      SET i = i + LENGTH(result) + LENGTH(delim);
    END IF;
    RETURN NEXT result;
  END WHILE;
  RETURN;
END //
DELIMITER ;



CREATE PROCEDURE GetSalesShareWithVAT
AS
BEGIN
    WITH DailySales AS (
        SELECT
            dd.d AS SalesDate,
            ds.store_name AS StoreName,
            dg.group_name AS GoodsGroupName,
            SUM(CAST(fc.sale_grs AS DECIMAL(10,2))) AS TotalSalesAmountWithVAT
        FROM
            fct_cheque fc
        JOIN
            dim_date dd ON fc.date_id = dd.did
        JOIN
            dim_stores ds ON fc.store_id = ds.store_id
        JOIN
            dim_goods dg ON fc.good_id = dg.good_id
        GROUP BY
            dd.d,
            ds.store_name,
            dg.group_name
    ),
    TotalDailySales AS (
        SELECT
            SalesDate,
            StoreName,
            SUM(TotalSalesAmountWithVAT) AS TotalDailySalesWithVAT
        FROM
            DailySales
        GROUP BY
            SalesDate,
            StoreName
    )
    SELECT
        ds.SalesDate,
        ds.StoreName,
        ds.GoodsGroupName,
        ds.TotalSalesAmountWithVAT,
        (ds.TotalSalesAmountWithVAT / ISNULL(tds.TotalDailySalesWithVAT, 1)) * 100 AS SalesShareWithVATPercentage
    FROM
        DailySales ds
    LEFT JOIN
        TotalDailySales tds ON ds.SalesDate = tds.SalesDate AND ds.StoreName = tds.StoreName
    ORDER BY
        SalesShareWithVATPercentage DESC;
END;
